mapper.select:
SELECT *
FROM reservation r
JOIN members m ON r.user_id = m.user_seq
WHERE r.status = #{status}
AND m.user_id = #{userId}

mapper.insert:
insert into reservation (
reservation_id, user_id, store_seq, reservation_date, reservation_time, num_guests,
status, created_at, updated_at
) values (
#{reservationId}, #{userId}, #{storeSeq}, #{reservationDate}, #{reservationTime},
#{numGuests}, #{status}, #{createdAt}, #{updatedAt}
)
